fun Context.goLogin() {
    Intent(this, ActivityLogin::class.java).also { startActivity(it) }
}

fun Context.goListMovies() {
    Intent(this, MainActivity::class.java)
        .also { startActivity(it) }
}

fun Context.gotDetailsMovie(movieViewModel: MovieViewModel) {
    Intent(this, DetailsActivity::class.java).also { intent ->
        intent.putExtra(MOVIE_EXTRA, movieViewModel)
        startActivity(intent)
    }
}

fun View.show() {
    visibility = View.VISIBLE
}

fun View.hide() {
    visibility = View.GONE
}

fun getImagePath(movie: MovieViewModel) = "https://image.tmdb.org/t/p/original${movie.pathImage}"